import React from 'react'
import { render } from 'react-dom';
import { View, Text, StyleSheet, Image } from 'react-native'
const {useEffect} = React;

const Pokemon = ({ pokemon }) => {
    /*useEffect(() => {
        console.log(pokemon);
    })*/

    const urlImage = 'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/';
    let url = pokemon.url;
    const idPokemon = url.split('https://pokeapi.co/api/v2/pokemon/');
    //console.log(idPokemon);
    const link = urlImage + idPokemon[1].substring(0, idPokemon[1].length - 1) + ".svg";

    return (
        <View style={styles.item}>
            <Image
                style={styles.image}
                resizeMode='contain'
                source={{ uri: link }}
            />
            <Text style={styles.pagi}>{item.name}</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
      backgroundColor: '#141414',
      padding: 20,
      flex: 1 // Para que me ocupe todo el tamanio de la pantalla 
    },
    header: {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: 10,
      color: '#FFFFFF',
  
  },
  poke:{
      color: '#FFFFFF',
      fontSize: 30
  },
  pagi:{
      color: '#FFFFFF'
  },
  item:{
    flex: 1,
    color:'#FFFFFF',
    backgroundColor: '#141414'
  },
  image: {
    width: 100,
    height: 100
  }
  });

export default Pokemon;
