import React, { Component } from 'react';

// Cuando cargue la app, quiero hacer una peticion GET --> Usamos useEffect
import { View, Text, StyleSheet, StatusBar, FlatList, PanResponder } from 'react-native';
import { Navbar } from '../components/Navbar';
import Buscador from '../components/Searchbar';

const {useState, useEffect} = React;

export class PokemonsScreen extends Component {

  constructor(props){
    super(props);
    this.state = { pokemons: [] };
    this.getPokemons();
  }

  setPokemons(pokes) {
    this.setState({ pokemons: pokes });
  }

  getPokemons = async (limit=10, offset=0) => {
    try {
      let url = `https://pokeapi.co/api/v2/pokemon?limit=${limit}&offset=${offset}`;
      const response = await fetch(url);
      const data = await response.json();
      this.setPokemons(data.results);
      console.log(this.state.pokemons);
    } catch (err) {
    }
  }

  render() {
    return (

      <View style={styles.container}>
        <Navbar />
        <Buscador style={styles.buscador} />
      <View>
      <View style={styles.header}>
        <Text style={styles.poke}>Pokedex</Text>
        <Text style={styles.pagi}>Pagination</Text>
     </View>
       {this.state.pokemons.map((pokemon, idx) => {
         return (
           <div>{pokemon.name}</div>
         )
       })}
    </View>
    </View>
    )
  }
}


const styles = StyleSheet.create({
  container: {
    backgroundColor: '#141414',
    padding: 20,
    flex: 1 // Para que me ocupe todo el tamanio de la pantalla
  },
  header: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
    color: '#FFFFFF'

},
poke:{
    color: '#FFFFFF',
    fontSize: 30
},
pagi:{
    color: '#FFFFFF'
},
list:{
    width: '90%',
    color: '#FFFFFF'
}
});
