import React, { Component } from 'react';

// Cuando cargue la app, quiero hacer una peticion GET --> Usamos useEffect
import { View, Text, StyleSheet, StatusBar, FlatList, PanResponder } from 'react-native';
import { Navbar } from '../components/Navbar';
import Buscador from '../components/Searchbar';
import Pokemon from '../components/Pokemon';
import axios from 'axios';

const {useState, useEffect} = React;

export class PokemonsScreen extends Component {

  constructor(props){
    super(props);
    this.state = { pokemons: [] };
    //this.fetchPokemons();
    this.getPokemons();
  }

  setPokemons(pokes) {
    this.setState({ pokemons: pokes });
  }

  getPokemons = async (limit=20, offset=0) => {
      axios.get(`https://pokeapi.co/api/v2/pokemon?limit=${limit}&offset=${offset}`)
      .then((response) => {
        console.log(response.data.results);
        this.setPokemons(response.data.results);
      }).catch((err) => {
        console.log(err);
      })
  }

  /*getPokemonData = async (url) =>{
    try {
        const resp = await fetch(url);
        const data = await resp.json();
        return data;
    } catch (err) {
      
    }
  }

  fetchPokemons = async () => {
    try {
      const data = await this.getPokemons();
      console.log(data.results);
      const promises = data.results.map(async (pokemon) => {
         return await this.getPokemonData(pokemon.url)
      })
      const results = await Promise.all();
      this.setPokemons(results);
    } catch (err) {
    }
  }*/

  render() {
    return (

      <View style={styles.container}>
        <Navbar />
        <Buscador style={styles.buscador} />
      <View>
      <View style={styles.header}>
        <Text style={styles.poke}>Pokedex</Text>
        <Text style={styles.pagi}>Pagination</Text>
     </View>
     
       
    </View>
    </View>
    )
  }
}


const styles = StyleSheet.create({
  container: {
    backgroundColor: '#141414',
    padding: 20,
    flex: 1 // Para que me ocupe todo el tamanio de la pantalla
  },
  header: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
    color: '#FFFFFF'

},
poke:{
    color: '#FFFFFF',
    fontSize: 30
},
pagi:{
    color: '#FFFFFF'
},
list:{
    width: '90%',
    color: '#FFFFFF'
}
});
