import React from 'react'
import { View, Text, StyleSheet, Image } from 'react-native'
import {Card, Title } from 'react-native-paper';
const { useEffect } = React;

const Pokemon = ({ pokemon }) => {
    /*useEffect(() => {
        console.log(pokemon);
    })*/

    const urlImage = 'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/';
    let url = pokemon.url;
    const idPokemon = url.split('https://pokeapi.co/api/v2/pokemon/');
    //console.log(idPokemon);
    const link = urlImage + idPokemon[1].substring(0, idPokemon[1].length - 1) + ".svg";

    return (
        <View style={styles.containerItem}>
            <Card style={styles.card}>
                <Card.Title title={pokemon.name} />
                <Card.Cover style={styles.imag} source={{ uri: link }} />
            </Card>

        </View>
    )
}

const styles = StyleSheet.create({
    containerItem: {
        justifyContent: 'center',
        alignItems: 'center',
        flex: 1,
        marginHorizontal: -30,
        display: 'grid'

    },
    pagi: {
        color: '#FFFFFF'
    },
    card: {
        width: 160,
        height: 110
    },
    imag:{
        width: 150,
        height: 90
    }
});

export default Pokemon;
