import React, { Component } from 'react';
// Cuando cargue la app, quiero hacer una peticion GET --> Usamos useEffect
import { View, Text, StyleSheet, StatusBar } from 'react-native';
import { configureFonts } from 'react-native-paper';
import Navbar from '../components/Navbar';
import Buscador from '../components/Searchbar';

export class PokemonsScreen extends Component {
    render() {
      return (
        <View style={styles.container}>
          <Navbar style={styles.nav}> </Navbar>
          <Searchbar />
        </View>
      )
    }
  }

  const styles = StyleSheet.create({
    container: {
      backgroundColor: '#141414',
      alignItems: 'center',
      textAlign: 'center',
      flex: 1 // Para que me ocupe todo el tamanio de la pantalla
    },
    nav:{
        height: 100,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-evenly',
        alignItems: 'center'
    }
  })
