import React, { Component } from 'react';

// Cuando cargue la app, quiero hacer una peticion GET --> Usamos useEffect
import { View, Text, StyleSheet, StatusBar } from 'react-native';
import { Navbar } from '../components/Navbar';
import Buscador from '../components/Searchbar';
import Pokedex from '../components/Pokedex';

const {useState, useEffect} = React;

const PokemonsScreen = () => {
 const [pokemons, setPokemons] = useState([]);


  const getPokemons = async (limit=10, offset=0) => {
    try {
      let url = `https://pokeapi.co/api/v2/pokemon/?limit=${limit}&offset=${offset}`;
      await axios.get(url).then(resp => setPokemons(resp.data));
    } catch (err) {

    }
  }
  useEffect(() => {
    getPokemons();
  }, [input])

    return (
      <View style={styles.container}>
        <Navbar />
        <Buscador style={styles.buscador} />
        <Pokedex pokemons={pokemons}/>
      </View>
    )
  }


const styles = StyleSheet.create({
  container: {
    backgroundColor: '#141414',
    padding: 20,
    flex: 1 // Para que me ocupe todo el tamanio de la pantalla
  }
});

export default PokemonsScreen;
