import React, { Component } from 'react';

// Cuando cargue la app, quiero hacer una peticion GET --> Usamos useEffect
import { View, Text, StyleSheet, StatusBar } from 'react-native';
import { Navbar } from '../components/Navbar';
import Buscador from '../components/Searchbar';
import Pokedex from '../components/Pokedex';

const {useState, useEffect} = React;

const PokemonsScreen = () => {

 const [pokemons, setPokemons] = useState([]);

  const getPokemons = (limit=10, offset=0) => {
    try {
      axios.get(`https://pokeapi.co/api/v2/pokemon/?limit=${limit}&offset=${offset}`).then(resp => setPokemons(resp.data.results));
    } catch (err) {

    }
  }
  useEffect(() => {
    getPokemons();
  }, []);

    return (

      <View style={styles.container}>
        <Navbar />
        <Buscador style={styles.buscador} />
        <Pokedex/>
      <View>
      <View style={styles.header}>
        <Text style={styles.poke}>Pokedex</Text>
        <Text style={styles.pagi}>Pagination</Text>
     </View>
     <View style={styles.pokeGrid}>
       <View>
           {pokemons.map((pokemon, idx) =>{
               return(
                   <div>{pokemon.name}</div>
               )
           })}
       </View>
     </View>
    </View>
    </View>
    )
  }


const styles = StyleSheet.create({
  container: {
    backgroundColor: '#141414',
    padding: 20,
    flex: 1 // Para que me ocupe todo el tamanio de la pantalla
  },
  header: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10

},
poke:{
    color: '#FFFFFF',
    fontSize: 30
},
pagi:{
    color: '#FFFFFF'
},
pokeGrid:{
    display: 'grid',
    color: '#FFFFFF'
}
});

export default PokemonsScreen;
