import React, { Component } from 'react';
import { StyleSheet, View, Text } from 'react-native';

const Pokedex = (props) => {
    const {pokemons} = props; //Es igual que decir const pokemons = props.pokemons;
    return(
        <View>
         <View style={styles.header}>
           <Text style={styles.poke}>Pokedex</Text>
           <Text style={styles.pagi}>Pagination</Text>
        </View>
        <View style={styles.pokeGrid}>
          <View>
              {pokemons.map((pokemon, idx) =>{
                  return(
                      <div>{pokemon.name}</div>
                  )
              })}
          </View>
        </View>
       </View>
    )
}

const styles = StyleSheet.create({
    header: {
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: 10

    },
    poke:{
        color: '#FFFFFF',
        fontSize: 30
    },
    pagi:{
        color: '#FFFFFF'
    },
    pokeGrid:{
        display: 'grid',
        color: '#FFFFFF'
    }
});

export default Pokedex;
