import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph } from 'react-native-paper';

const LeftContent = props => <Avatar.Icon {...props} icon="folder" />

const Pokemon = (props) => {
    try {
        const {pokemon} = props;
    } catch (err) { 
        const pokemon = [];
    }
    
    if (pokemon != []) {
        return (
            <Card>
            <Card.Title title={pokemon.name} subtitle="Card Subtitle" left={LeftContent} />
            <Card.Content>
              <Title>{pokemon.name}</Title>
              <Paragraph>
                  {pokemon.types.map((type, idx) =>{
                      return <div key={idx}> {type.name} </div>
                  })}
              </Paragraph>
            </Card.Content>
            <Card.Cover source={ pokemon.sprites.front_default } />
          </Card>
        )
    } else {  
    }
    
}

export default Pokemon;
